mapper.select:
SELECT * FROM GOODS G JOIN CART C ON G.GOODS_CD = C.GOODS_CD AND G.GOODS_CD IN
#{goodsCd}
ORDER BY C.ENROLL_DT DESC

mapper.update:
UPDATE
		GOODS
SET
		STOCK = STOCK - #{orderGoodsQty}
WHERE
		GOODS_CD = #{goodsCd}

UPDATE
		MEMBER
SET
		POINT = POINT + #{point}
WHERE
		MEMBER_ID = #{memberId}

update.foreach:
UPDATE
		GOODS
SET
		STOCK = STOCK - #{goodsMap.orderGoodsQty}
WHERE
		GOODS_CD = #{goodsMap.goodsCd}

mapper.update:
UPDATE
		MEMBER
SET
		POINT = POINT + #{point}
WHERE
		MEMBER_ID = #{memberId}
